import React, { useState, useEffect } from 'react';
import axios from 'axios';

import logo from './imgs/background.png';
import './App.css';

function App() {

  const [weatherData, setWeatherData] = useState(null);

  useEffect(() => { 
    axios.get('http://localhost:3001/api/Cali')
      .then(response => { 
        setWeatherData(response.data);
      })
      .catch(error => {
        console.error('Error al obtener datos del clima:', error);
      });
  }, []);


  return (
    <div className="App">

      <div className="App-header">

        <img  src={logo} className="App-logo" alt="logo" />

        <div>
      <h1>Información del clima para {weatherData.name}</h1>
      <p>Condiciones climáticas: {weatherData.weather.w.main}</p>
      <p>Temperatura: {weatherData.main.temp} K</p>
      <p>Humedad: {weatherData.main.humidity}%</p>
      <p>Velocidad del viento: {weatherData.wind.speed} m/s</p>
      <p>País: {weatherData.sys.country}</p>
    </div>

      </div>
    </div>
  );

}

export default App;
