import React, { useState, useEffect } from 'react';
import logo from './imgs/background.png';
import './App.css';

function App() {
  const [data, setData] = useState(null);

  useEffect(() => {
    // Simulando la obtención del JSON mediante una API fetch
    fetch('http://localhost:3001/api/Cali')
      .then(response => response.json())
      .then(data => setData(data));
  }, []);

  return (
    <div className="App">

      <div className="App-header">

        <img  src={logo} className="App-logo" alt="logo" />

        <div> 
      <p>Condiciones climáticas: {weatherData.weather.w.main}</p>
      <p>Temperatura: {weatherData.main.temp} K</p>
      <p>Humedad: {weatherData.main.humidity}%</p>
      <p>Velocidad del viento: {weatherData.wind.speed} m/s</p>
      <p>País: {weatherData.sys.country}</p>
    </div>

      </div>
    </div>
  );

}

export default App;
