import React, { useState, useEffect } from 'react';
import axios from 'axios';

import logo from './imgs/background1.png';

import btn1 from './icons/temp.png';
import btn2 from './icons/humidity.png';
import btn3 from './icons/wind.png';


import './App.css';

function App() {


  const fecha = new Date();
  const dia = fecha.getDate();
  const mes = fecha.toLocaleString('es-ES', { month: 'long' });
  const anio = fecha.getFullYear();

  // Formatear la fecha
  const fechaFormateada = `${dia} ${mes} ${anio}`;

  const [data, setData] = useState(null);


  useEffect(() => {
    axios.get('http://localhost:3001/201')
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        console.error('Error al obtener datos:', error);
      });
  }, [])


  //  <p>Temperatura: {data.main.temp - 273.15}°C</p>
  //  <p>Humedad: {data.main.humidity}%</p>
  //  <p>Velocidad: {data.wind.speed}</p>

  return (
    <div className="App">

      <div className="App-header">

        <div class="header-txt">
          <div id="t1"> santiago de cali</div>
          <div id="t2"> {fechaFormateada} </div>
          <div id="t3"> 31&deg;C </div>
        </div>

        <img src={logo} className="App-logo" alt="logo" />

        <div class="info-txt">
          <ul>
            <li>
              <div id="t1"><img src={btn1} className="App-logo" alt="logo" /> </div>
              <div id="t2">TEXTO</div>
              <div id="t3">00</div>
            </li>
            <li>
              <div id="t1"><img src={btn1} className="App-logo" alt="logo" /> </div>
              <div id="t2">TEXTO</div>
              <div id="t3">00</div>
            </li>
            <li>
              <div id="t1"><img src={btn1} className="App-logo" alt="logo" /> </div>
              <div id="t2">TEXTO</div>
              <div id="t3">00</div>
            </li>           
          </ul>
        </div>



      </div>
    </div>
  );

}

export default App;
